// The purpose of the <Business /> component is to represent how a business (a restaurant) in Ravenous will be formatted and styled
// For now, we’ll hard code a single business listing. Later, you’ll update it dynamically using the Yelp API
import React from 'react';
import ReactDOM from 'react-dom';
import './Business.css';

const business = {
  imageSrc: 'https://s3.amazonaws.com/codecademy-content/programs/react/ravenous/pizza.jpg',
  name: 'MarginOtto Pizzeria',
  address: '1010 Paddington Way',
  city: 'Flavortown',
  state: 'NY',
  zipCode: '10101',
  category: 'Italian',
  rating: 4.5,
  reviewCount: 90
};

class Business extends React.component {
  render() {
    return (
      <div className="Business">
        <div className="image-container">
          <img src={business.imageSrc} alt={business.name}/>
        </div>
        <h2>{business.name}</h2>
          <div className="Business-information">
            <div className="Business-address">
              <p>{business.adress}</p>
              <p>{business.city}</p>
              <p>{business.state} {business.zipCode}</p>
            </div>
          <div className="Business-reviews">
            <h3>{business.category}</h3>
              <h3 className="rating">{business.rating} stars</h3>
                <p>{business.reviewCount} reviews</p>
          </div>
        </div>
      </div>
    );
  }
};

export default Business;
